mapper.select:

WHERE (SALE_MEM_ID = #{memId} OR BUY_MEM_ID = #{memId})
	AND WRITER_ID != #{memId}
ORDER BY regdate desc


WHERE SALE_MEM_ID = #{memId}
	AND WRITER_ID != #{memId}
ORDER BY regdate desc


WHERE BUY_MEM_ID = #{memId}
	AND WRITER_ID != #{memId}
ORDER BY regdate desc

SELECT COUNT(ID) FROM SALE_REVIEW_VIEW
WHERE (SALE_MEM_ID = #{memId} OR BUY_MEM_ID = #{memId})
	AND WRITER_ID != #{memId}

SELECT COUNT(ID) FROM SALE_REVIEW_VIEW
WHERE SALE_MEM_ID = #{memId}
	AND WRITER_ID != #{memId}

SELECT COUNT(ID) FROM SALE_REVIEW_VIEW
WHERE BUY_MEM_ID = #{memId}
	AND WRITER_ID != #{memId}
